		<span class="token keyword">int</span> op<span class="token operator">=</span><span class="token function">cj</span><span class="token punctuation">(</span>p<span class="token punctuation">[</span>id1<span class="token punctuation">]</span><span class="token punctuation">.</span>x <span class="token punctuation">,</span>p<span class="token punctuation">[</span>id1<span class="token punctuation">]</span><span class="token punctuation">.</span>y <span class="token punctuation">,</span>p<span class="token punctuation">[</span>id2<span class="token punctuation">]</span><span class="token punctuation">.</span>x <span class="token punctuation">,</span>p<span class="token punctuation">[</span>id2<span class="token punctuation">]</span><span class="token punctuation">.</span>y <span class="token punctuation">)</span><span class="token punctuation">;</span>
		ans<span class="token operator">+=</span>op<span class="token punctuation">;</span>
